const article = $input.item.json;
const translateRequest = $('Parse Translate Request').first().json; // SEULE référence autorisée ici

console.log('Préparation traduction:');
console.log('- Article ID:', article.id);
console.log('- Langue cible:', translateRequest.target_language);

return [{
  json: {
    id: article.id,
    topic: article.topic,
    content: article.content,
    table_of_contents: article.table_of_contents,
    plan_options: article.plan_options,
    search_synthesis: article.search_synthesis,
    evaluation_details: article.evaluation_details,
    parameters: article.parameters || {},
    score: article.score, //  AJOUTER
    drive_link: article.drive_link, //  AJOUTER
    target_language: translateRequest.target_language,
    article_id: translateRequest.article_id
  }
}];

const input = $input.item.json;
const previousData = $input.first().json;

let translatedData;
let output = input.output || "";

try {
  // Extraction RAPIDE : éviter les regex sur tout le contenu
  let cleanJson = output.trim();

  // Nettoyage des blocs de code markdown si présents
  if (cleanJson.startsWith('```')) {
    cleanJson = cleanJson.replace(/^```(?:json)?\n?/, '').replace(/\n?```$/, '').trim();
  }

  // Extraction sécurisée entre la première { et la dernière }
  const startIdx = cleanJson.indexOf('{');
  const endIdx = cleanJson.lastIndexOf('}');

  if (startIdx !== -1 && endIdx !== -1) {
    cleanJson = cleanJson.substring(startIdx, endIdx + 1);
  }

  translatedData = JSON.parse(cleanJson);
  console.log('✅ Traduction parsée avec succès');
} catch (e) {
  console.error("❌ Parse Error:", e.message);
  translatedData = {};
}

return [{
  json: {
    article_id: previousData.article_id,
    new_language: previousData.target_language,
    translated_topic: translatedData.translated_topic || previousData.topic,
    translated_content: translatedData.translated_content || previousData.content,
    translated_toc: translatedData.translated_toc || previousData.table_of_contents,
    translated_plan_options: translatedData.translated_plan_options || previousData.plan_options,
    translated_search_synthesis: translatedData.translated_search_synthesis || previousData.search_synthesis,
    translated_evaluation_details: translatedData.translated_evaluation_details || previousData.evaluation_details,
    parameters: previousData.parameters,
    score: previousData.score, // ✅ PASSER score
    drive_link: previousData.drive_link // ✅ PASSER drive_link
  }
}];

const input = $input.item.json;
const body = input.body || input;

return {
  json: {
    article_id: body.article_id,
    target_language: body.target_language,
    current_status: body.current_status || 'published', // Fallback to published if missing
    timestamp: new Date().toISOString()
  }
};

const input = $input.item.json;
const previousData = $input.first().json;

const newContent = input.output || '';

return [{
  json: {
    article_id: previousData.article_id,
    new_content: newContent
  }
}];

const body = $input.item.json.body;

return {
  json: {
    article_id: body.article_id,
    instructions: body.instructions,
    selection: body.selection,
    timestamp: new Date().toISOString()
  }
};

