const article = $input.item.json;
const modifyRequest = $input.first().json;

console.log('=== PREPARER MODIFICATION ===');
console.log('- Article ID:', article.id);
console.log('- Scope:', modifyRequest.scope);
console.log('- Target section:', modifyRequest.target_section);

// Contexte des fichiers joints
let filesContext = '';
if (modifyRequest.attached_files && modifyRequest.attached_files.length > 0) {
  filesContext = '\n\n## FICHIERS JOINTS\n';
  modifyRequest.attached_files.forEach((file, index) => {
    filesContext += `${index + 1}. ${file.name || file.url}\n`;
    filesContext += `   URL: ${file.url}\n`;
  });
}

// Préparer les données selon le scope
let dataToModify = {};
let instruction = '';

switch (modifyRequest.scope) {
  case 'title_only':
    // Seulement le titre principal
    dataToModify = {
      title: article.table_of_contents?.title || 'Sans titre'
    };
    instruction = `Tu dois UNIQUEMENT modifier le TITRE PRINCIPAL de l'article. Ne touche pas aux sections.`;
    console.log('Modification: TITRE PRINCIPAL uniquement');
    break;

  case 'section_specific':
    // Section spécifique par index
    const sectionIndex = modifyRequest.target_section;
    const targetSection = article.table_of_contents?.sections?.[sectionIndex];

    if (targetSection) {
      dataToModify = {
        title: article.table_of_contents.title,
        target_section_index: sectionIndex,
        target_section: targetSection,
        total_sections: article.table_of_contents.sections.length
      };
      instruction = `Tu dois UNIQUEMENT modifier la SECTION ${sectionIndex + 1}. Les autres sections doivent rester EXACTEMENT identiques.`;
      console.log(`Modification: SECTION ${sectionIndex + 1} uniquement`);
    } else {
      console.error('Section introuvable');
      dataToModify = article.table_of_contents;
      instruction = 'La section demandée est introuvable. Modifie ce qui est pertinent.';
    }
    break;

  case 'section_named':
    // Section spécifique par nom
    const sectionName = modifyRequest.target_section;
    const sections = article.table_of_contents?.sections || [];
    const foundIndex = sections.findIndex(s => 
      s.title.toLowerCase().includes(sectionName.toLowerCase())
    );

    if (foundIndex !== -1) {
      dataToModify = {
        title: article.table_of_contents.title,
        target_section_index: foundIndex,
        target_section: sections[foundIndex],
        total_sections: sections.length
      };
      instruction = `Tu dois UNIQUEMENT modifier la section "${sectionName}" (index ${foundIndex + 1}). Les autres sections restent identiques.`;
      console.log(`Modification: SECTION "${sectionName}" trouvée (index ${foundIndex})`);
    } else {
      console.error('Section nommée introuvable');
      dataToModify = article.table_of_contents;
      instruction = 'La section nommée est introuvable. Modifie ce qui est pertinent.';
    }
    break;

  case 'full':
  default:
    // Modification complète
    dataToModify = article.table_of_contents;
    instruction = 'Tu peux modifier librement le TOC selon les instructions.';
    console.log('Modification: TOC COMPLET');
    break;
}

return [{
  json: {
    article_id: article.id,
    full_toc: article.table_of_contents, // TOC complet pour référence
