      { name: 'Anti-IA', score: 20, explanation: 'Excellent (fallback)' }
    ],
    feedback_for_writer: 'Évaluation par défaut'
  };
}

console.log('ÉVALUATION FINALE:');
console.log('- Score global:', evaluation.global_score);
console.log('- Nombre de critères:', evaluation.criteria.length);
console.log('- Feedback:', evaluation.feedback_for_writer ? 'Présent' : 'Absent');

return [{
  json: {
    ...previousData,
    evaluation: evaluation,
    current_score: evaluation.global_score,
